INSERT INTO `wp_newsletter_emails` VALUES (1,'','','<!DOCTYPE html>\n<html>\n<head>\n<title>{email_subject}</title>\n<meta charset=\"utf-8\">\n<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\n<style type=\"text/css\">\n#outlook a{padding:0;} /* Force Outlook to provide a \"view in browser\" message */\n.ReadMsgBody{width:100%;} .ExternalClass{width:100%;} /* Force Hotmail to display emails at full width */\n.ExternalClass, .ExternalClass p, .ExternalClass span, .ExternalClass font, .ExternalClass td, .ExternalClass div {line-height: 100%;} /* Force Hotmail to display normal line spacing */\nbody, table, td, a{-webkit-text-size-adjust:100%; -ms-text-size-adjust:100%;} /* Prevent WebKit and Windows mobile changing default text sizes */\ntable, td{mso-table-lspace:0pt; mso-table-rspace:0pt;} /* Remove spacing between tables in Outlook 2007 and up */\nimg{-ms-interpolation-mode:bicubic;} /* Allow smoother rendering of resized image in Internet Explorer */\n\nbody{margin:0; padding:0; height:100% !important; margin:0; padding:0; width:100% !important;}\nimg{border:0; height:auto; line-height:100%; outline:none; text-decoration:none; max-width: 100%!important}\ntable{border-collapse:collapse !important;}\nimg.aligncenter{display:block;margin:0 auto;}\n\n@media all and (max-width: 525px) {\n\n    /* ALLOWS FOR FLUID TABLES */\n    table[class=\"wrapper\"]{\n        width:100% !important;\n    }\n\n    table[class=\"mobile-full-width\"] {\n        width:100% !important;\n    }\n\n    img[class=\"mobile-full-width\"] {\n        width:100% !important;\n        display: block;\n    }\n\n    /* ADJUSTS LAYOUT OF LOGO IMAGE */\n    td[class=\"logo\"]{\n        text-align: left;\n        padding: 20px 0 20px 0 !important;\n    }\n\n    td[class=\"logo\"] img{\n        margin:0 auto!important;\n    }\n\n    /* USE THESE CLASSES TO HIDE CONTENT ON MOBILE */\n    td[class=\"mobile-hide\"]{\n        display:none;}\n\n    img[class=\"mobile-hide\"]{\n        display: none !important;\n    }\n\n    img[class=\"img-max\"]{\n        max-width: 100% !important;\n        /*width: 100% !important;*/\n        height:auto !important;\n    }\n\n    /* FULL-WIDTH TABLES */\n    table[class=\"responsive-table\"]{\n        width:100%!important;\n        max-width: 100%!important;\n    }\n\n    /* UTILITY CLASSES FOR ADJUSTING PADDING ON MOBILE */\n    td[class=\"padding\"]{\n        padding: 10px 5% 15px 5% !important;\n    }\n\n    td[class=\"padding-copy\"]{\n        padding: 10px 5% 10px 5% !important;\n        text-align: center;\n    }\n\n    td[class=\"padding-meta\"]{\n        padding: 30px 5% 0px 5% !important;\n        text-align: center;\n    }\n\n    td[class=\"no-pad\"]{\n        padding: 0 0 20px 0 !important;\n    }\n\n    td[class=\"no-padding\"]{\n        padding: 0 !important;\n    }\n\n    td[class=\"section-padding\"]{\n        padding: 50px 15px 50px 15px !important;\n    }\n\n    td[class=\"section-padding-bottom-image\"]{\n        padding: 50px 15px 0 15px !important;\n    }\n\n    /* ADJUST BUTTONS ON MOBILE */\n    td[class=\"mobile-wrapper\"]{\n        padding: 10px 5% 15px 5% !important;\n    }\n\n    table[class=\"mobile-button-container\"]{\n        margin:0 auto;\n        width:100% !important;\n    }\n\n    a[class=\"mobile-button\"]{\n        width:80% !important;\n        padding: 15px !important;\n        border: 0 !important;\n        font-size: 16px !important;\n    }\n}\n\n/* Text */\n\n\n/* Last posts */\n@media (max-width: 525px) {\n    .posts-1-column {\n        width: 100%!important;\n    }\n    .posts-1-image {\n        width: 100%!important;\n        display: block;\n    }\n}\n\n\n\n/* Html */\n.html-td-global p {\n    font-family: Helvetica, Arial, sans-serif;\n    font-size: 16px;\n}\n\n\n/* Hero */\n@media all and (max-width: 525px) {\n    .hero-table {\n        width: 100%!important;\n        max-width: 100%!important;\n    }\n\n    .hero-table-right {\n        margin-top: 20px;\n    }\n}\n\n/* Header */\n@media all and (max-width: 640) {\n    .header-table td {\n        width: 100%!important;\n    }\n    .header-logo-global {\n        text-align: center!important;\n    }\n}\n\n</style>\n</head>\n<body style=\"margin: 0; padding: 0;\" dir=\"ltr\">\n\n<table cellpadding=\'0\' cellspacing=\'0\' border=\'0\' width=\'100%\'>\n<tr>\n<td bgcolor=\'#f4f4f4\' valign=\'top\'><!-- tnp -->\r\n\r\n\n<!-- /tnp -->\n</td>\n</tr>\n</table>\n\n</body>\n</html>','2020-10-11 12:01:16','new',0,0,0,1,0,'message','select * from wp_newsletter where status=\'C\'',2,'','','This email requires a modern e-mail reader but you can view the email online here:\n{email_url}.\nThank you, Dev-espot\nTo change your subscription follow: {profile_url}.',NULL,1602417676,'5264082780','a:1:{s:19:\"composer_background\";s:7:\"#f4f4f4\";}',0,0,'',0,0,0,0);
